#!/usr/bin/env bash
php artisan crud:generate Brand --fields="name#string#required;slug#string;description#text#required;filename#string#required;featured#boolean#required;meta_title#string#required;meta_description#text#required;meta_slug#string#required" --route=yes --pk=_id --view-path="admin" --controller-namespace=Admin
php artisan crud:generate Coupon --fields="name#string#required|unique;value#string#required;magnitude#string#required;type#string#required;expired_at#date#required;minimum_cart#integer#required;maximum_cart#integer#nullable|integer;limit_usage_by_coupon#integer#nullable|integer;limit_usage_by_user#integer#nullable|integer;single_use#boolean#boolean;emails#string" --route=yes --pk=_id --view-path="admin" --controller-namespace=Admin
php artisan crud:generate Lead --fields="email#string#required|unique|email;type#string#required" --route=yes --pk=_id --view-path="admin" --controller-namespace=Admin
php artisan crud:generate Country --fields="name#string#required;active#boolean#required;provinces#string" --route=yes --pk=_id --view-path="admin" --controller-namespace=Admin
php artisan crud:generate Category --fields="name#string#required;slug#string#required|unique;filename#string#required|image;products#integer;meta_title#string#required;meta_description#string#required;meta_slug#string#required" --route=yes --pk=_id --view-path="admin" --controller-namespace=Admin
php artisan crud:generate Faq --fields="name#string#required;answer#text#required" --route=yes --pk=_id --view-path="admin" --controller-namespace=Admin

php artisan crud:generate Product --fields="name#string; slug#string; status#integer; introduction#text; description#text; meta_title#string; meta_description#string; meta_slug#string" --validations="name#required; slug#required; status#required; introduction#required; description#required; meta_title#required; meta_description#required; meta_slug#required" --route=yes --pk=_id --view-path="admin" --controller-namespace=Admin --localize=yes